    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String session = Session.getText();
        
        String frame = Frame.getText();
        String plain = Plain.getText();
        if (plain.isEmpty()){
            JOptionPane.showMessageDialog(this, "insert plain to encrypt", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
            
        if (a.setKeys(session, frame)){
            String encrypted = a.encrypt(plain);
            Encrypted.setText(encrypted);
        }else {
            JOptionPane.showMessageDialog(this, "Session = 64 bit & frame = 22 bit", "Error", JOptionPane.ERROR_MESSAGE);
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
       String cipher = Cipher.getText();
        
            
       
        if (cipher.isEmpty()){
            JOptionPane.showMessageDialog(this, "insert cipher to decrypt", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        String decrypted = a.encrypt(cipher);
            
        Decrypted.setText(decrypted);
    }//GEN-LAST:event_jButton2ActionPerformed

