    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
         String session = Session.getText();
        String frame = Frame.getText();
         if (a.setKeys(session, frame)){
            Session_binary.setText(a.getSessionKey());
            Frame_binary.setText(a.getFrameKey());
            a.generateKeys();
            a.setGenerated(true);
            R1.setText(a.getRegisterValues(1));
            R2.setText(a.getRegisterValues(2));
            R3.setText(a.getRegisterValues(3));
            keystream_binary.setText(a.getKeyStream());
        }else {
            JOptionPane.showMessageDialog(this, "Session = 64 bit & frame = 22 bit", "Error", JOptionPane.ERROR_MESSAGE);
        }
        
    }//GEN-LAST:event_jButton4ActionPerformed

